import React from 'react';

const HeroSection = () => {
  return (
    <div className="min-h-screen bg-[#0f172a] text-white px-6 py-10 flex flex-col items-center justify-center relative">
      {/* Grid Background */}
      <div className="absolute inset-0 bg-gradient-to-r from-transparent to-[#1e293b] opacity-30 pointer-events-none" />
      
      {/* Header Text */}
      <h2 className="text-xl font-medium text-yellow-300 mb-2">Hey ðŸ‘‹ I'm</h2>
      <h1 className="text-5xl font-bold text-white mb-2 text-center">Asfakur Nariz</h1>
      <p className="text-gray-400 text-lg mb-2">A passionate</p>
      <p className="text-2xl text-pink-500 font-semibold italic mb-2">Full Stack Developer</p>
      <p className="text-gray-400 text-center max-w-xl mb-6">
        specialized in building stunning pixel-perfect interactive websites/applications.
      </p>

      {/* Profile Image */}
      <img 
        src="https://via.placeholder.com/150" // Replace with actual image path
        alt="Asfakur Nariz"
        className="rounded-md w-40 h-40 object-cover mb-6 grayscale"
      />

      {/* Resume Button */}
      <a 
        href="#" 
        className="px-6 py-2 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full font-semibold text-white flex items-center space-x-2"
      >
        <span>Resume</span>
        <svg className="w-5 h-5" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" d="M4 16v1a2 2 0 002 2h12a2 2 0 002-2v-1M12 12v8m0 0l-4-4m4 4l4-4M4 4h16" />
        </svg>
      </a>
    </div>
  );
};

export default HeroSection;
